public class Movie{    private long id;    private String title;    private int rating;    private double duration;    public Movie()    {        this.title = title;        this.rating = rating;        this.duration = duration;    }    public long getId() {        return id;    }    public void setId(long id) {        this.id = id;    }    public String getTitle() {        return title;    }    public void setTitle(String title) {        this.title = title;    }    public int getRating() {        return rating;    }    public void setRating(int rating) {        this.rating = rating;    }    public double getDuration() {        return duration;    }    public void setDuration(double duration) {        this.duration = duration;    }}