import java.util.ArrayList;public class MovieService{    private Movie movie;    private String currentMovie;    private ArrayList<String> movieList;    public MovieService()    {        this.movie = new Movie();        this.currentMovie = currentMovie;        this.movieList = new ArrayList<>();    }    public void addMovies(String currentMovie)    {        movieList.add(currentMovie);    }    public Movie getMovie() {        return movie;    }    public void setMovie(Movie movie) {        this.movie = movie;    }    public String getCurrentMovie() {        return currentMovie;    }    public void setCurrentMovie(String currentMovie) {        this.currentMovie = currentMovie;    }    public ArrayList<String> getMovieList() {        return movieList;    }    public void setMovieList(ArrayList<String> movieList) {        this.movieList = movieList;    }}